mapper.select:
select m_num, m_name, launch_date, genre, poster, end_date, m_price, content
from MOVIE
where m_num=#{mnum}

select m_num, m_name, launch_date, genre, poster, end_date, m_price, content
from MOVIE
where m_name like #{mname}

select count(m_num)
from MOVIE
where m_num like #{mnum}

select m_num, m_name, launch_date, genre, poster, end_date, m_price, content
from MOVIE
where genre like #{genre}

select count(m_num)
from MOVIE
where genre like #{genre}

select m_num, m_name, launch_date, genre, poster, end_date, m_price, content
from MOVIE
where content like #{content}

select count(m_num)
from MOVIE
where content like #{content}
